  </div>
  <script>
   // Function to open sidebar
   function openSidebar() {
       var sidebar = document.getElementById('logo-sidebar');
       sidebar.classList.add('translate-x-0');
   }

   // Function to close sidebar
   function closeSidebar() {
       var sidebar = document.getElementById('logo-sidebar');
       sidebar.classList.remove('translate-x-0');
   }

   // Toggle sidebar when clicking on menu button
   document.querySelector('[data-drawer-toggle="logo-sidebar"]').addEventListener('click', function() {
       var sidebar = document.getElementById('logo-sidebar');
       if (sidebar.classList.contains('translate-x-0')) {
           closeSidebar();
       } else {
           openSidebar();
       }
   });

   // Close sidebar when clicking on any menu item
   var menuItems = document.querySelectorAll('#logo-sidebar a');
   menuItems.forEach(function(item) {
       item.addEventListener('click', function() {
           closeSidebar();
       });
   });

   function hideAdminText() {
        var adminText = document.getElementById('userTitle');
        if (window.innerWidth <= 768) {
            adminText.style.display = 'none';
        } else {
            adminText.style.display = 'block';
        }
    }

    // Gọi hàm hideAdminText khi trang tải hoặc cửa sổ được resize
    window.addEventListener('load', hideAdminText);
    window.addEventListener('resize', hideAdminText);
</script>


